// Simplified highlight.js for code syntax highlighting in WPS Office style
window.hljs = {
    highlightAll: function() {
        const codeBlocks = document.querySelectorAll('pre code, code');
        codeBlocks.forEach(block => {
            if (!block.classList.contains('hljs')) {
                block.classList.add('hljs');
                block.style.background = '#f8f9fa';
                block.style.color = '#e83e8c';
                block.style.padding = '2px 6px';
                block.style.borderRadius = '3px';
                block.style.fontFamily = 'Consolas, Monaco, Courier New, monospace';
                block.style.border = '1px solid #e9ecef';
                block.style.fontSize = '13px';
            }
        });
        
        // Style pre blocks
        const preBlocks = document.querySelectorAll('pre');
        preBlocks.forEach(block => {
            if (!block.classList.contains('hljs')) {
                block.classList.add('hljs');
                block.style.background = '#f8f9fa';
                block.style.color = '#212529';
                block.style.border = '1px solid #dee2e6';
                block.style.borderRadius = '6px';
                block.style.padding = '16px';
                block.style.fontFamily = 'Consolas, Monaco, Courier New, monospace';
                block.style.fontSize = '13px';
                block.style.lineHeight = '1.5';
                block.style.overflowX = 'auto';
                block.style.margin = '12px 0';
                block.style.boxShadow = 'inset 0 1px 3px rgba(0,0,0,0.1)';
            }
        });
    }
};
